import React, { useState } from 'react'
import PropTypes from 'prop-types'
import { Link } from "react-router-dom";
import { useParams, useHistory } from 'react-router-dom';

const EditProduct = ({ products, onUpdate }) => {
    let { id } = useParams();
    let history = useHistory();
    const product = products.find(product => product.id === id);

    const [currentProduct, setCurrentProduct] = useState(product);

    const onHandleSubmit = (e) => {
        e.preventDefault();
        onUpdate(currentProduct);
        history.push('/admin/products');
    }
    const onHandleChange = e => {
        const { name, value } = e.target;
        setCurrentProduct({
            ...currentProduct,
            [name]: value
        })
    }

    return (
        <div className="card shadow mb-4">
            <div className="card-header py-3">
                <h6 className="m-0 font-weight-bold text-primary">Sửa sản phẩm</h6>
            </div>
            <div className="card-body">
                <form action="" onSubmit={onHandleSubmit}>
                    <div className="form-group">
                        <label htmlFor="">Tên sản phẩm</label>
                        <input className="form-control" type="text" placeholder="" name="name" defaultValue={product.name} onChange={onHandleChange} />
                    </div>
                    <div className="form-group">
                        <label htmlFor="">Giá sản phẩm</label>
                        <input className="form-control" type="text" placeholder="" name="price" defaultValue={product.price} onChange={onHandleChange} />
                    </div>
                    <div className="form-group">
                        <label htmlFor="">Ảnh</label>
                        <input className="form-control" type="text" placeholder="" name="image" defaultValue={product.image} onChange={onHandleChange} />
                    </div>

                    <button type="submit" className="btn btn-primary mr-2">Cập nhật</button>
                    <Link to="/admin/products">
                        <a className="btn btn-danger text-white">Trở lại</a>
                    </Link>
                </form>
            </div>
        </div >
    )
}

EditProduct.propTypes = {

}

export default EditProduct
