import React from "react";
import { Link } from "react-router-dom";
import parse from "html-react-parser";

const Categories = ({ categories, onRemove, products }) => {
  function removeElement(id) {
    onRemove(id);
  }
  // console.log(id);
  // const post = posts.find((post) => post.id === id);
  // const qty = products.find((qty) => qty.category === id);
  console.log(products.length);
  if (products.category === categories.id) {
    const qty = products.length;
    console.log(qty);
  }

  return (
    <div>
      <div className="card shadow mb-4">
        <div className="card-header py-3">
          <h6 className="m-0 font-weight-bold text-primary">Sản phẩm</h6>
        </div>
        <div className="card-body">
          <Link to="/admin/add-category">
            <button className="btn btn-primary">Thêm danh mục</button>
          </Link>

          <div className="table-responsive mt-3">
            <table
              className="table table-striped"
              id="dataTable"
              width="100%"
              cellSpacing={0}
            >
              <thead>
                <tr>
                  <th scope="col">STT</th>
                  <th scope="col">Tên</th>
                  <th scope="col">Ảnh</th>
                  <th scope="col">Mô tả</th>
                  <th scope="col">Tùy chọn</th>
                </tr>
              </thead>
              <tbody>
                {categories.map(({ id, name, image, text }, index) => (
                  <tr id={"row-" + id} key={index}>
                    <th scope="row">{index + 1}</th>
                    <td>
                      <Link to={`/admin/detail-product/${id}`}>{name}</Link>
                    </td>
                    <td>
                      <img src={image} width="80"></img>
                    </td>
                    <td>{text}</td>
                    <td>
                      <Link
                        to={`/admin/edit-category/${id}`}
                        className="btn btn-success"
                      >
                        Cập nhật
                      </Link>{" "}
                      &nbsp;
                      <button
                        onClick={() => removeElement(id)}
                        type=" button"
                        className="btn btn-danger"
                      >
                        Xóa
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
};

Categories.propTypes = {};

export default Categories;
