import React, { useState } from "react";
import PropTypes from "prop-types";
import { Link, useHistory } from "react-router-dom";
import { useForm } from "react-hook-form";

const AddForm = ({ onAdd }) => {
  const { register, handleSubmit, watch, errors } = useForm();
  const [valueInput, setvalueInput] = useState({});
  let history = useHistory();

  const onHandleSubmit = (data) => {
    //console.log(data);
    onAdd(data);
    //console.log(1);
    history.push("/admin/products");
  };
  return (
    <div className="card shadow mb-4">
      <div className="card-header py-3">
        <h6 className="m-0 font-weight-bold text-primary">Thêm sản phẩm</h6>
      </div>
      <div className="card-body">
        <form action="" onSubmit={handleSubmit(onHandleSubmit)}>
          <div className="form-group">
            <label htmlFor="">Tên sản phẩm</label>
            <input
              className="form-control"
              type="text"
              placeholder=""
              name="name"
              ref={register({ required: true, pattern: /^\S{1}.{0,24}$/i })}
            />
            <small id="imageHelp" className="form-text text-danger">
              {errors.name && <span>Tên không bao gồm dấu cách ở đầu và </span>}
            </small>
          </div>
          <div className="form-group">
            <label htmlFor="">Giá sản phẩm</label>
            <input
              className="form-control"
              type="text"
              placeholder=""
              name="price"
              ref={register({ required: true, pattern: /^[0-9]{1,9}$/i })}
            />
            <small id="imageHelp" className="form-text text-danger">
              {errors.price && (
                <span>Giá bao gồm các số từ 0-9 nhiều nhất 10 chữ số</span>
              )}
            </small>
          </div>
          <div className="form-group">
            <label htmlFor="">Ảnh</label>
            <input
              className="form-control"
              type="text"
              placeholder=""
              name="image"
              ref={register({ required: true })}
            />
            <small id="imageHelp" className="form-text text-danger">
              {errors.image && <span>Ảnh không được để trống</span>}
            </small>
          </div>

          <button type="submit" className="btn btn-primary mr-2">
            Thêm
          </button>
          <Link to="/admin/products">
            <a className="btn btn-danger text-white">Trở lại</a>
          </Link>
        </form>
      </div>
    </div>
  );
};

AddForm.propTypes = {};

export default AddForm;
