import React, { useState } from "react";
import { Link } from "react-router-dom";
import { useParams, useHistory } from "react-router-dom";
import { useForm } from "react-hook-form";

const EditCatePost = ({ onAdd, cateposts }) => {
  let { id } = useParams();
  let history = useHistory();
  const catepost = cateposts.find((catepost) => cateposts.id === id);
  const { register, handleSubmit, watch, errors } = useForm();
  const onHandleSubmit = (data) => {
    const newData = {
      id: catepost.id,
      ...data,
    };
    onAdd(newData);
    alert("Cập nhật danh mục bài viết thành công");
    history.push("/admin/cate-post");
  };
  return (
    <div>
      <div className="card shadow mb-4">
        <div className="card-header py-3">
          <h6 className="m-0 font-weight-bold text-primary">
            Danh mục bài viết
          </h6>
        </div>
        <div className="card-body">
          <Link to="/admin/add-catepost">
            <button className="btn btn-primary">Thêm danh mục</button>
          </Link>

          <div className="table-responsive mt-3">
            <table className="table table-striped" width="100%" cellSpacing={0}>
              <thead>
                <tr>
                  <th scope="col">STT</th>
                  <th scope="col">Tên danh mục</th>
                  <th scope="col">Tiêu đề</th>
                  <th scope="col">Tùy chọn</th>
                </tr>
              </thead>
              <tbody>
                {cateposts.map(({ id, name, content }, index) => (
                  <tr id={"row-" + id} key={index}>
                    <th scope="row">{index + 1}</th>
                    <td>{name}</td>
                    <td>{content}</td>
                    <td>
                      <Link
                        to={`/admin/edit-catepost/${id}`}
                        className="btn btn-success"
                      >
                        Cập nhật
                      </Link>{" "}
                      &nbsp;
                      <button
                        onClick={() => removeElement(id)}
                        type=" button"
                        className="btn btn-danger"
                      >
                        XÓA
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
};

EditCatePost.propTypes = {};

export default EditCatePost;
