import React, { useState } from 'react'
import Sidebar from '../../components/Admin/Sidebar'
import Topbar from '../../components/Admin/TopBar'
import Footer from '../../components/Admin/Footer'
import Product from '../../pages/views/Admin/Products'
import DetailProduct from '../../pages/views/Admin/DetailProduct'
import AddForm from '../../pages/views/Admin/AddForm'
import EditProduct from '../../pages/views/Admin/EditProduct'
import dataFake from '../../dataFake';
import '../../assets/css/admin/sb-admin-2.min.scss'
import '../../assets/css/admin/main.scss'
import axios from 'axios';
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link,
    useParams
} from "react-router-dom";

export default ({ children }) => {
    var apiUrl = 'https://5f201329fba6d400169d4909.mockapi.io/categories/1/products';
    axios.get(apiUrl)
        .then(data_hotel => {
            var data = data_hotel.data
            var content = ``;
            data.forEach(element => {
                var id_hotel = element.id;
                var apiUrl2 = apiUrl + '/' + id_hotel + "/rooms";
                var hotel_name = `${element.name}`;
                document.querySelector('#select_hotel').innerHTML += `<option value="${element.id}">${element.name}</option>`;
                axios.get(apiUrl2)
                    .then(res => {
                        if (res.statusText === "OK") {
                            var data_room = res.data;
                            data_room.forEach(element => {
                                content += `<tr id="row-${element.id}">
                                  <td>${element.id}</td>
                                  <td>${element.room_name}</td>
                                  <td id="hotel_name">${hotel_name}</td>
                                  <td>${element.floor}</td>
                                  <td>
                                      <img src="${element.image}" style="width: 100px; height: 100px; border-radius: 0;" class="img img-avatar"/>
                                  </td>
                                  <td>${element.price}</td>
                                  <td>  
                                      <a href="edit-room.html?id=${element.id}&hotelId=${element.hotelId}" class="btn btn-sm btn-primary">Edit</a>  
                                      <button class="btn btn-sm btn-danger"  onclick="removeElement(${element.id})">Remove</button>  
                                  </td>
                              </tr>`;
                            });
                            console.log(data_room);
                            document.querySelector('#list_room').innerHTML = content;
                        }
                    })
            })
        });

    const [products, setproducts] = useState(dataFake);
    const removeElement = (id) => {
        console.log(id);
        const newData = products.filter(product => product.id !== id);
        console.log(newData);
        setproducts(newData);
    }

    const onHandleAdd = (product) => {
        console.log(product);
        setproducts([
            ...products,
            product
        ])

    }
    return (
        <div className="admin-page">
            <Router>
                <div id="wrapper">
                    <Sidebar />
                    <div id="content-wrapper" className="d-flex flex-column">
                        <div id="content">
                            <Topbar />
                            <div className="container-fluid">
                                <Switch>
                                    <Route exact path="/admin/">
                                        Home page
                                </Route>
                                    <Route path="/admin/products">
                                        <Product products={products} onRemove={removeElement} />
                                    </Route>
                                    <Route path="/admin/add-product">
                                        <AddForm onAdd={onHandleAdd} />
                                    </Route>
                                    <Route path="/admin/detail-product/:id">
                                        <DetailProduct products={products} />
                                    </Route>
                                    <Route path="/admin/edit-product/:id">
                                        <EditProduct products={products} />
                                    </Route>
                                </Switch>
                            </div>
                        </div>
                        <Footer />
                    </div>
                </div>
            </Router>
        </div>
    )
}
