import React, { useState } from 'react'
import PropTypes from 'prop-types'
import { Link } from "react-router-dom";

const AddForm = ({ onAdd }) => {
    const [valueInput, setvalueInput] = useState({});
    const onHandleChange = (e) => {
        const { name, value } = e.target;

        setvalueInput({
            ...valueInput,
            [name]: value
        })
        console.log(valueInput);
    }

    const onHandleSubmit = (e) => {
        e.preventDefault();

        onAdd(valueInput);
        //console.log(1);
    }
    return (
        <div className="card shadow mb-4">
            <div className="card-header py-3">
                <h6 className="m-0 font-weight-bold text-primary">Thêm sản phẩm</h6>
            </div>
            <div className="card-body">
                <form action="" onSubmit={onHandleSubmit}>
                    <div className="form-group">
                        <label htmlFor="">Tên sản phẩm</label>
                        <input className="form-control" type="text" placeholder="" name="name" onChange={onHandleChange} />
                    </div>
                    <div className="form-group">
                        <label htmlFor="">Giá sản phẩm</label>
                        <input className="form-control" type="text" placeholder="" name="price" onChange={onHandleChange} />
                    </div>
                    <div className="form-group">
                        <label htmlFor="">Ảnh</label>
                        <input className="form-control" type="text" placeholder="" name="image" onChange={onHandleChange} />
                    </div>

                    <button type="submit" className="btn btn-primary mr-2">Thêm</button>
                    <Link to="/admin/products">
                        <a className="btn btn-danger text-white">Trở lại</a>
                    </Link>
                </form>
            </div>
        </div >
    )
}

AddForm.propTypes = {

}

export default AddForm
